import axios from 'axios';
import React, { useState } from 'react';

const QRCodeGenerator = () => {
    const [qrCodeUrl, setQrCodeUrl] = useState('');
    const [redirectUrl, setRedirectUrl] = useState('');
    const [brandName, setBrandName] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [message, setMessage] = useState('');

    // Generate QR Code
    const generateQrCode = async () => {
        try {
            const response = await axios.post('http://localhost:7000/api/generate', { redirect_url: redirectUrl });
            setQrCodeUrl(response.data.qrCodeUrl);
            setMessage('QR Code generated successfully!');
        } catch (error) {
            console.error('Error generating QR code:', error);
            setMessage('Error generating QR code.');
        }
    };

    // Book Slot for Brand
    const bookSlot = async () => {
        try {
            const qrCodeId = await getQrCodeId(); // Function to retrieve the generated QR code ID
            await axios.post('http://localhost:7000/api/book', {
                qrCodeId,
                brandName,
                startTime,
                endTime,
                redirectUrl
            });
            setMessage('Slot booked successfully!');
        } catch (error) {
            console.error('Error booking slot:', error);
            setMessage('Error booking slot.');
        }
    };

    // Placeholder function for getting QR code ID
    const getQrCodeId = async () => {
        // You might want to save the QR Code ID after generating it and return it here.
        // For this example, return a dummy ID.
        return 'DUMMY_QR_CODE_ID';
    };

    return (
        <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
            <h2>QR Code Manager</h2>
            <div>
                <h3>Generate QR Code</h3>
                <label>
                    Redirect URL:
                    <input
                        type="text"
                        value={redirectUrl}
                        onChange={(e) => setRedirectUrl(e.target.value)}
                    />
                </label>
                <button onClick={generateQrCode}>Generate QR Code</button>
                {qrCodeUrl && (
                    <div>
                        <h4>Generated QR Code:</h4>
                        <img src={qrCodeUrl} alt="QR Code" />
                    </div>
                )}
            </div>

            <div>
                <h3>Book Slot for Brand</h3>
                <label>
                    Brand Name:
                    <input
                        type="text"
                        value={brandName}
                        onChange={(e) => setBrandName(e.target.value)}
                    />
                </label>
                <label>
                    Start Time:
                    <input
                        type="datetime-local"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                    />
                </label>
                <label>
                    End Time:
                    <input
                        type="datetime-local"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                    />
                </label>
                <button onClick={bookSlot}>Book Slot</button>
            </div>

            {message && <p>{message}</p>}
        </div>
    );
};

export default QRCodeGenerator;
