// src/QrCodeGenerator.js
import { QRCodeCanvas } from 'qrcode.react'; // Use QRCodeCanvas instead
import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:7000');

const QrCodeGenerator = () => {
    const [dynamicUrl, setDynamicUrl] = useState('');
    const [notification, setNotification] = useState('');
    const [qrCodeValue, setQrCodeValue] = useState('http://localhost:7000/api/redirect'); // Static redirect URL

    useEffect(() => {
        // Listen for URL updates via WebSocket
        socket.on('urlUpdated', (newUrl) => {
            setQrCodeValue(newUrl); // Update the QR code value dynamically
            setNotification(`The dynamic URL has been updated to: ${newUrl}`);
        });

        return () => {
            socket.off('urlUpdated');
        };
    }, []);

    const handleSetUrl = async () => {
        const response = await fetch('http://localhost:7000/api/set-dynamic-url', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ url: dynamicUrl }),
        });

        if (response.ok) {
            alert('URL updated successfully');
            // No need to generate QR code again as it updates via WebSocket
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>QR Code Generator</h1>
            <input
                type="text"
                placeholder="Dynamic URL"
                value={dynamicUrl}
                onChange={(e) => setDynamicUrl(e.target.value)}
            />
            <button onClick={handleSetUrl}>Set Dynamic URL</button>
            <QRCodeCanvas value={qrCodeValue} size={256} /> {/* Use QRCodeCanvas */}
            {notification && <p>{notification}</p>}
        </div>
    );
};

export default QrCodeGenerator;
