import { QRCodeCanvas } from 'qrcode.react'; // or use QRCodeSVG if you prefer SVG output
import React, { useState } from 'react';

const DynamicQRCodeGenerator = () => {
  // State to hold the URL entered by the user
  const [url, setUrl] = useState('');

  // Function to handle changes in the input field
  const handleUrlChange = (event) => {
    setUrl(event.target.value); // Update the URL state with user input
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>Dynamic QR Code Generator</h2>

      {/* Input field for the URL */}
      <input
        type="text"
        value={url}
        onChange={handleUrlChange} // Update the URL when the input changes
        placeholder="Enter URL here"
        style={{ width: '300px', height: '30px', fontSize: '16px', padding: '5px', marginBottom: '20px' }}
      />

      {/* Only show QR code if URL is not empty */}
      {url && (
        <div>
          <h3>Generated QR Code:</h3>
          <QRCodeCanvas
            value={url} // The URL entered by the user is the data for the QR code
            size={256} // Size of the QR code
            level="H" // Error correction level (H is the highest)
          />
        </div>
      )}

      {/* Optional: Display the URL below the QR code */}
      <p style={{ marginTop: '20px' }}>
        <strong>QR Code for:</strong> {url || 'Enter a URL above'}
      </p>
    </div>
  );
};

export default DynamicQRCodeGenerator;
