// src/QrCodeGenerator.js
import { QRCodeSVG } from 'qrcode.react'; // Use QRCodeSVG for rendering
import React, { useState } from 'react';

const QrCodeGenerator = () => {
    const [url, setUrl] = useState(''); // State to hold the input URL

    const handleChange = (e) => {
        setUrl(e.target.value); // Update URL state as user types
    };

    return (
        <div className="qr-code-generator">
            <h1>QR Code Generator</h1>
            <input
                type="text"
                placeholder="Enter URL"
                value={url}
                onChange={handleChange}
            />
            {url && (
                <div className="qr-code">
                    <p>Scan the QR code to visit:</p>
                    <a href={url} target="_blank" rel="noopener noreferrer">{url}</a>
                    <QRCodeSVG value={url} size={256} />
                </div>
            )}
        </div>
    );
};

export default QrCodeGenerator;
