import axios from 'axios';
import React, { useState } from 'react';

const QRCodeGenerator = () => {
    const [redirectUrl, setRedirectUrl] = useState('');
    const [qrCodeData, setQrCodeData] = useState(null);
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [bookingMessage, setBookingMessage] = useState('');

    const handleGenerateQRCode = async () => {
        try {
            const response = await axios.post('http://localhost:7000/api/generate', { redirect_url: redirectUrl });
            setQrCodeData(response.data);
            setBookingMessage(''); // Reset any previous booking message
        } catch (error) {
            console.error('Error generating QR code:', error);
            alert('Failed to generate QR code.');
        }
    };

    const handleBookSlot = async () => {
        if (!qrCodeData || !qrCodeData.qrCodeId) {
            alert('Please generate a QR code first.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:7000/api/book', {
                qrCodeId: qrCodeData.qrCodeId,
                redirectUrl,
                startTime,
                endTime,
            });
            setBookingMessage(response.data.message);
        } catch (error) {
            console.error('Error booking time slot:', error);
            alert('Failed to book time slot.');
        }
    };

    return (
        <div style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>
            <h2>QR Code Manager</h2>
            <div style={{ marginBottom: '20px' }}>
                <h4>Generate QR Code</h4>
                <input
                    type="text"
                    placeholder="Enter redirect URL"
                    value={redirectUrl}
                    onChange={(e) => setRedirectUrl(e.target.value)}
                    style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                />
                <button onClick={handleGenerateQRCode} style={{ width: '100%', padding: '10px', background: '#167f6e', color: 'white', border: 'none' }}>
                    Generate QR Code
                </button>
                {qrCodeData && (
                    <div style={{ marginTop: '20px', textAlign: 'center' }}>
                        <img src={qrCodeData.qrCodeUrl} alt="Generated QR Code" style={{ width: '200px', height: '200px' }} />
                        <p><strong>QR Code ID:</strong> {qrCodeData.qrCodeId}</p>
                    </div>
                )}
            </div>

            {qrCodeData && (
                <div style={{ marginTop: '20px' }}>
                    <h4>Book Time Slot</h4>
                    <input
                        type="datetime-local"
                        placeholder="Start Time"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                        style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                    />
                    <input
                        type="datetime-local"
                        placeholder="End Time"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                        style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                    />
                    <button onClick={handleBookSlot} style={{ width: '100%', padding: '10px', background: '#167f6e', color: 'white', border: 'none' }}>
                        Book Slot
                    </button>
                    {bookingMessage && <p style={{ color: 'green', marginTop: '10px' }}>{bookingMessage}</p>}
                </div>
            )}
        </div>
    );
};

export default QRCodeGenerator;
