// src/QrCodeGenerator.js
import { QRCodeSVG } from 'qrcode.react'; // Use QRCodeSVG for rendering
import React, { useState } from 'react';

const QrCodeGenerator = () => {
    const [url, setUrl] = useState(''); // State to hold the input URL
    const [generatedUrl, setGeneratedUrl] = useState(''); // State to hold the generated QR code URL

    const handleChange = (e) => {
        setUrl(e.target.value); // Update URL state as user types
    };

    const handleGenerate = () => {
        // Set the generated URL only when the user submits a valid link
        if (url) {
            setGeneratedUrl(url); // Store the generated URL
        }
    };

    return (
        <div className="qr-code-generator">
            <h1>QR Code Generator</h1>
            <input
                type="text"
                placeholder="Enter URL"
                value={url}
                onChange={handleChange}
            />
            <button onClick={handleGenerate}>Generate QR Code</button>
            {generatedUrl && (
                <div className="qr-code">
                    <p>Scan the QR code to visit:</p>
                    <a href={generatedUrl} target="_blank" rel="noopener noreferrer">{generatedUrl}</a>
                    <QRCodeSVG value={generatedUrl} size={256} />
                </div>
            )}
            <div>
                <p>Current Link (Dynamic Update): {url}</p>
                {/* Display the current link dynamically */}
            </div>
        </div>
    );
};

export default QrCodeGenerator;
