import axios from 'axios';
import React, { useState } from 'react';

const QRCodeGenerator = () => {
    const [landingPageUrl, setLandingPageUrl] = useState(''); // State to hold the landing page URL
    const [qrCodeImage, setQrCodeImage] = useState(''); // State to hold the QR code image

    // Function to handle QR code generation
    const handleGenerateQR = async () => {
        try {
            // Make POST request to backend to generate QR code
            const response = await axios.post('http://localhost:7000/api/test', { 
                default_url: landingPageUrl, // Make sure to use the correct field name
                location: 'lucknow' // You might want to add the location as well
            });

            // Access the qr_code_image from newQrCode
            setQrCodeImage(response.data.newQrCode.qr_code_image); // Set the QR code image state
        } catch (error) {
            console.error("Error generating QR code:", error); // Log any errors
        }
    };

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>Generate QR Code</h2>
            <input 
                type="text" 
                value={landingPageUrl} 
                onChange={(e) => setLandingPageUrl(e.target.value)} 
                placeholder="Enter landing page URL" 
                style={{ width: '300px', padding: '10px', margin: '10px 0' }}
            />
            <button 
                onClick={handleGenerateQR} 
                style={{ padding: '10px 20px', backgroundColor: '#167f6e', color: '#fff', border: 'none', borderRadius: '5px' }}
            >
                Generate QR Code
            </button>

            {qrCodeImage && (
                <div style={{ marginTop: '20px' }}>
                    <h3>Your QR Code:</h3>
                    <img 
                        src={qrCodeImage} 
                        alt="QR Code" 
                        style={{ width: '200px', height: '200px' }} 
                    /> {/* Display the QR code image */}
                </div>
            )}
        </div>
    );
};

export default QRCodeGenerator;
