// src/components/QrCodeAdvertisement.js
import axios from 'axios';
import { QRCodeCanvas as QRCode } from 'qrcode.react'; // Use QRCodeCanvas if QRCode is not available directly
import React, { useState } from 'react';

const QRCodeGenerator = () => {
    const [redirectUrl, setRedirectUrl] = useState('');
    const [qrCodeData, setQrCodeData] = useState(null);
    const [qrCodeId, setQrCodeId] = useState(''); // Added QR Code ID for booking time slot
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [slotRedirectUrl, setSlotRedirectUrl] = useState('');

    const handleGenerateQrCode = async () => {
        try {
            const response = await axios.post('http://localhost:7000/api/generate', { redirectUrl });
            setQrCodeData(response.data);
        } catch (error) {
            console.error('Error generating QR code:', error);
            alert('Failed to generate QR code.');
        }
    };

    const handleBookTimeSlot = async () => {
        try {
            await axios.post('http://localhost:7000/api/book', {
                qrCodeId,
                slotRedirectUrl,
                startTime,
                endTime
            });
            alert('Time slot booked successfully!');
        } catch (error) {
            console.error('Error booking time slot:', error);
            alert('Failed to book time slot.');
        }
    };

    return (
        <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
            <h1>QR Code Advertisement Service</h1>

            <h2>Generate QR Code</h2>
            <input
                type="text"
                placeholder="Enter redirect URL"
                value={redirectUrl}
                onChange={(e) => setRedirectUrl(e.target.value)}
                style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <button onClick={handleGenerateQrCode} style={{ padding: '10px', width: '100%' }}>
                Generate QR Code
            </button>

            {qrCodeData && (
                <div style={{ marginTop: '20px', textAlign: 'center' }}>
                    <h3>Your QR Code:</h3>
                    <QRCode value={qrCodeData.qrCodeUrl} size={256} />
                    <p>QR Code ID: {qrCodeData.qrCodeId}</p>
                </div>
            )}

            <h2 style={{ marginTop: '40px' }}>Book Time Slot</h2>
            <input
                type="text"
                placeholder="Enter QR Code ID"
                value={qrCodeId}
                onChange={(e) => setQrCodeId(e.target.value)}
                style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <input
                type="text"
                placeholder="Enter redirect URL for time slot"
                value={slotRedirectUrl}
                onChange={(e) => setSlotRedirectUrl(e.target.value)}
                style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <input
                type="datetime-local"
                value={startTime}
                onChange={(e) => setStartTime(e.target.value)}
                style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <input
                type="datetime-local"
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
                style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <button onClick={handleBookTimeSlot} style={{ padding: '10px', width: '100%' }}>
                Book Time Slot
            </button>
        </div>
    );
};

export default QRCodeGenerator;
