import { QRCodeCanvas } from 'qrcode.react'; // Import QRCodeCanvas (or QRCodeSVG)
import React, { useState } from 'react';

const DynamicQRCode = () => {
  const [url, setUrl] = useState('https://example.com'); // initial URL

  const handleUrlChange = (event) => {
    setUrl(event.target.value); // Update the URL based on user input
  };

  return (
    <div>
      <h2>Dynamic QR Code Generator</h2>

      {/* Input to change the destination URL */}
      <input 
        type="text" 
        value={url}
        onChange={handleUrlChange} 
        placeholder="Enter URL" 
        style={{ width: '300px', marginBottom: '20px' }}
      />

      {/* Display QR code for the current URL using QRCodeCanvas */}
      <QRCodeCanvas value={url} size={256} level="H" />

      <p>
        Scan the QR code to go to the link: <strong>{url}</strong>
      </p>
    </div>
  );
};

export default DynamicQRCode;
