import { QRCodeCanvas } from 'qrcode.react'; // or QRCodeSVG for SVG
import React, { useState } from 'react';

const DynamicQRCodeGenerator = () => {
  // State to hold the URL entered by the user
  const [url, setUrl] = useState('');
  // State to store the updated URL
  const [updatedUrl, setUpdatedUrl] = useState('');

  // Function to handle changes in the URL input field
  const handleUrlChange = (event) => {
    setUrl(event.target.value); // Update the URL state with user input
  };

  // Function to handle the button click for generating the QR code
  const handleGenerateClick = () => {
    if (url) {
      // Simply render the QR code based on the entered URL
      setUpdatedUrl(url); // Set the value for the updated URL state
    } else {
      alert('Please enter a valid URL.');
    }
  };

  // Function to handle updating the QR code's destination URL
  const handleUpdateClick = () => {
    if (updatedUrl) {
      setUrl(updatedUrl); // Update the QR code's destination URL
    } else {
      alert('Please enter a URL to update.');
    }
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>Dynamic QR Code Generator</h2>

      {/* Input field for the URL */}
      <input
        type="text"
        value={url}
        onChange={handleUrlChange} // Update the URL when the input changes
        placeholder="Enter URL here"
        style={{ width: '300px', height: '30px', fontSize: '16px', padding: '5px', marginBottom: '20px' }}
      />

      {/* Generate Button */}
      <button
        onClick={handleGenerateClick}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          marginBottom: '20px',
        }}
      >
        Generate QR Code
      </button>

      {/* Only show QR code after the button is clicked */}
      {updatedUrl && (
        <div>
          <h3>Generated QR Code:</h3>
          <QRCodeCanvas
            value={updatedUrl} // The updated URL is the data for the QR code
            size={256} // Same size of QR code
            level="H" // Error correction level (H is the highest)
          />
        </div>
      )}

      {/* Optional: Display the URL below the QR code */}
      {updatedUrl && (
        <p style={{ marginTop: '20px' }}>
          <strong>QR Code for:</strong> {updatedUrl}
        </p>
      )}

      {/* Input to update the URL for the QR code */}
      {updatedUrl && (
        <div style={{ marginTop: '20px' }}>
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)} // Update URL for QR code update
            placeholder="Enter new URL to update"
            style={{ width: '300px', height: '30px', fontSize: '16px', padding: '5px', marginRight: '10px' }}
          />
          <button
            onClick={handleUpdateClick} // Update the QR code with new URL
            style={{
              padding: '10px 20px',
              fontSize: '16px',
              cursor: 'pointer',
              backgroundColor: '#FF5733',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
            }}
          >
            Update QR Code
          </button>
        </div>
      )}
    </div>
  );
};

export default DynamicQRCodeGenerator;
