import axios from 'axios';
import React, { useState } from 'react';

const QRCodeGenerator = () => {
    const [url, setUrl] = useState('');
    const [qrCode, setQrCode] = useState('');
    const [brandName, setBrandName] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');

    const handleGenerateQRCode = async () => {
        try {
            const response = await axios.post('http://localhost:7000/api/generate', { redirect_url: url });
            setQrCode(response.data.qr_code_url);
        } catch (error) {
            console.error('Error generating QR code:', error);
        }
    };

    const handleBookSlot = async () => {
        try {
            await axios.post('http://localhost:7000/api/book', {
                brand_name: brandName,
                start_time: startTime,
                end_time: endTime,
                redirect_url: url,
            });
            alert('Slot booked successfully');
        } catch (error) {
            console.error('Error booking slot:', error);
        }
    };

    return (
        <div className="QRCodeAdvertisement">
            <h1>QR Code Advertisement System</h1>

            <div>
                <h2>Generate QR Code</h2>
                <input
                    type="text"
                    placeholder="Enter URL"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                />
                <button onClick={handleGenerateQRCode}>Generate QR Code</button>
                {qrCode && <img src={qrCode} alt="QR Code" style={{ marginTop: '10px' }} />}
            </div>

            <div>
                <h2>Book Slot for Brand</h2>
                <input
                    type="text"
                    placeholder="Brand Name"
                    value={brandName}
                    onChange={(e) => setBrandName(e.target.value)}
                />
                <input
                    type="datetime-local"
                    value={startTime}
                    onChange={(e) => setStartTime(e.target.value)}
                />
                <input
                    type="datetime-local"
                    value={endTime}
                    onChange={(e) => setEndTime(e.target.value)}
                />
                <button onClick={handleBookSlot}>Book Slot</button>
            </div>

            <style jsx>{`
                .QRCodeAdvertisement {
                    max-width: 600px;
                    margin: 0 auto;
                    text-align: center;
                }
                input {
                    display: block;
                    margin: 10px auto;
                    padding: 10px;
                    width: 80%;
                }
                button {
                    padding: 10px 15px;
                    background-color: #167f6e;
                    color: white;
                    border: none;
                    cursor: pointer;
                }
                button:hover {
                    background-color: #ab7465;
                }
            `}</style>
        </div>
    );
};

export default QRCodeGenerator;
