// src/QRCodeGenerator.js
import axios from 'axios';
import React, { useEffect, useState } from 'react';

const QRCodeGenerator = () => {
    const [url, setUrl] = useState('');
    const [qrCodeUrl, setQrCodeUrl] = useState('');
    const [isQRCodeGenerated, setIsQRCodeGenerated] = useState(false); // Track QR code generation

    // Function to fetch the QR code
    const fetchQRCode = async () => {
        try {
            const response = await axios.get('http://localhost:5000/generate-qrcode');
            setQrCodeUrl(response.data.qrCodeUrl);
            setIsQRCodeGenerated(true); // Set QR code generated to true
        } catch (error) {
            console.error('Error fetching QR code:', error);
        }
    };

    useEffect(() => {
        fetchQRCode(); // Fetch the QR code when the component mounts
    }, []);

    // Function to update the dynamic URL only if QR code is generated
    const updateDynamicUrl = async () => {
        if (!isQRCodeGenerated) {
            alert('Please generate the QR code first!');
            return;
        }

        try {
            await axios.post('http://localhost:5000/set-dynamic-url', { url });
            alert('Dynamic URL updated successfully!');
            fetchQRCode(); // Fetch the updated QR code after updating URL
        } catch (error) {
            console.error('Error updating dynamic URL:', error);
        }
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '50px' }}>
            <h1>Dynamic QR Code Generator</h1>
            {qrCodeUrl && <img src={qrCodeUrl} alt="QR Code" style={{ width: '200px', height: '200px' }} />}
            <div style={{ marginTop: '20px' }}>
                <input
                    type="text"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="Enter new dynamic URL"
                    style={{ padding: '10px', width: '300px', marginRight: '10px' }}
                />
                <button onClick={updateDynamicUrl} style={{ padding: '10px 20px' }}>
                    Update Dynamic URL
                </button>
            </div>
        </div>
    );
};

export default QRCodeGenerator;
